        class="piece"
        :class="getPieceShortName"
        :style="{
            transform: `translate(${elementPosition.x}px, ${elementPosition.y}px)`,
            cursor: isDragging ? 'grabbing' : 'grab'
        }"
        @mousedown="startDrag"
        @mousemove="dragElement"
        @mouseup="stopDrag"
